$(document).ready(function(){
  $(".owl-carousel").owlCarousel({
    stagePadding: 5,
    animateOut: 'slideOutDown',
    animateIn: 'flipInX',
    loop:true,
    margin:20,
    nav:false,
    responsive:{
        0:{
            items:1
        },
        600:{
            items:2
        },
        1000:{
            items:3
        }
    }
  });
});
