$(document).ready(function(){
  $(".owl-carousel").owlCarousel({
    stagePadding: 7,
    animateOut: 'fadeOut',
    autoplay:true,
    autoplayTimeout:2000,
    autoplayHoverPause:true,
    loop:true,
    margin:20,
    nav:false,
    responsive:{
        0:{
            items:1
        },
        600:{
            items:1
        },
        1000:{
            items:1
        }
    }
  });
});
